//search engine for articles, slides and media files 
func autoSearchIndex(w http.ResponseWriter, r *http.Request, IDX_TARGET string, FL_VALID_USER bool) string {
	
	c := appengine.NewContext(r)
	u := user.Current(c)
	
	var buffer3 bytes.Buffer
	
	uid := ""
	if u != nil {
		//uid = uid
		_, uid = checkSession(w,r)
	}
	
	index, err := search.Open(IDX_TARGET)
	if err != nil {
		 panic(err)
		//return ""
	}
 
	for t := index.List(c, nil); ; {
		var doc Doc
		id, err := t.Next(&doc)
		if err == search.Done {
			break
		}
		if err != nil {
			fmt.Fprintf(w, "Search error: %v\n", err)
			break
		}
 
		var p IDX_TDSSLIDE
		err = index.Get(c, id, &p)
		if err != nil {
			 panic(err)
			//return ""
		}
		
		FL_SEARCH_OK := false		
		
		if FL_VALID_USER == true {
			switch {
			
				case p.FL_SHARED == "Y" && (p.DOC_STAT == "Worldwide" || p.DOC_STAT == "ULAPPH Only" || p.DOC_STAT == "Personal" || p.DOC_STAT == "Premium"):
					FL_SEARCH_OK = true
					
				case p.DOC_AUTHOR == uid:
					FL_SEARCH_OK = true
					
			}
		} else {
			switch {
			
				case p.FL_SHARED == "Y" && p.DOC_STAT == "Worldwide":
					FL_SEARCH_OK = true
					
				case p.FL_SHARED == "Y" && p.DOC_STAT == "Premium":
					FL_SEARCH_OK = true
					
			}
			
		}
		
		if FL_SEARCH_OK == true {
			sValue := fmt.Sprintf("%v | %v | %v", p.DOC_KEY, strings.Replace(p.DOC_TITLE, "'", "", -1), strings.Replace(p.DOC_DESC, "'", "", -1))
			sData := searchSchemeHandler(w,r,p.CONTENT_URL,"Y")
			buffer3.WriteString(fmt.Sprintf("{ value: '%v', data: '%v' }, ", sValue, sData))
		}
	}
	
	return buffer3.String()
}

