				TASK_MEMCACHER_desktopWP_loginWP := ""
				wpKey := fmt.Sprintf("TASK_MEMCACHER_desktopWP_%v_%s", deskName, "loginWP")
				TASK_MEMCACHER_desktopWP_loginWP = getStrMemcacheValueByKey(w,r,wpKey)
 
