func timelineAddEventSlide(w http.ResponseWriter, r *http.Request, uid, GROUP_ID, contCat string, start, end int, tjs *Timelinejs, p *TDSSLIDE) bool {
	dks := TimelineEvent{}
	sd := TimelineStartDate{}
	ed := TimelineEndDate{}
	tm := TimelineEventMedia{}
	tt := TimelineText{}
	thisDU := str2int(p.DT_UPLOAD)
	flAdded := false
	if thisDU >= start && thisDU <= end && (p.AUTHOR == uid || GROUP_ID == "GRP_ADMIN" || GROUP_ID == "GRP_USER") {
		//fmt.Fprintf(w, "%v\n", p)
		//split DT_UPLOAD
		//20180824124749
		STR := fmt.Sprintf("%v", p.DT_UPLOAD)
		year := STR[0:4] //2018
		month := STR[4:6] //08
		day := STR[6:8] //24
		hour := STR[8:10] //12 
		minute := STR[10:12] //47
		sec := STR[12:14] //49
		sd.Year = year
		sd.Month = month
		sd.Day = day
		sd.Hour = hour
		sd.Minute = minute
		sd.Second = sec
		ed.Year = year
		ed.Month = month
		ed.Day = day
		ed.Hour = hour
		ed.Minute = minute
		ed.Second = sec
		//tm.URL = p.IMG_URL
		tm.URL = p.TAGS
		tt.Headline = p.TITLE
		///search?f=TDSMEDIA-UPD&q=50
		reqStr := fmt.Sprintf("%vslides?TYPE=SLIDE&MODE=NORMAL&PARM=LOOP&SECS=8&DOC_ID=%v&SID=TDSSLIDE-%v&MUSIC_ID=%v&GET_NEXT=%v&SOUND=%v&FL_COUNTRY_SPECIFIC=%v", getSchemeUrl(w,r), p.DOC_ID, p.DOC_ID, p.MUSIC_ID, p.GET_NEXT, SLIDE_SOUND_SET, p.FL_COUNTRY_SPECIFIC)
		tm.Link = reqStr
		tm.Caption = fmt.Sprintf("TDSSLIDE-%v/%v/%v", p.DOC_ID, "slide", "format")
		tm.Credit = "Copyright (c) ULAPPH Cloud Desktop" 
		tt.Text = fmt.Sprintf("%v<br>[ TDSSLIDE-%v ][ <a href=\"/search?f=TDSSLIDE-UPD&q=%v\" target=\"tjs\">Update</a> ][ <a href=\"%v\" target=\"tjs\">View</a> ]", p.DESC, p.DOC_ID, p.DOC_ID, reqStr)
		dks.StartDate = sd
		dks.EndDate = ed
		dks.Media = tm
		dks.Text = tt
		if contCat == "" {
			tjs.AllEvents = append(tjs.AllEvents, dks) 
			flAdded = true
		}else {
			if p.CATEGORY == contCat {
				//fmt.Fprintf(w, "%v\n", p)
				tjs.AllEvents = append(tjs.AllEvents, dks) 
				flAdded = true
			}

		}
	}
	return flAdded
}
