	cKey3 := fmt.Sprintf("ULAPPH_SCRT_%s_%v", UID, base64.StdEncoding.EncodeToString([]byte(stripchars(strings.TrimSpace(SEARCH_KEY)," "))))
	
	var lines []string
	var buffer5 bytes.Buffer
	sUrl := ShortenUrl(w,r,SEARCH_KEY)
