//handles bleve search which is capable of searching inside a document line by line 
func bleveSearch(w http.ResponseWriter, r *http.Request, uid, IDX_TARGET, SID, SEARCH_KEY string) {
	c := appengine.NewContext(r)
	
	if SEARCH_KEY == "" {
		bleveSearchOut(w,r,uid,IDX_TARGET,SID,"","","",SEARCH_KEY,nil)
		return
	}
	
	// open a new index
	mapping := bleve.NewIndexMapping()
	inBlev, err := bleve.NewMemOnly(mapping)
	if err != nil {
		fmt.Println(err)
		return
	}
 
	//open index
	index, err := search.Open(IDX_TARGET)
	if err != nil {
		 //panic(err)
	}
	
	var p IDX_TDSSLIDE
	err = index.Get(c, SID, &p)
	if err != nil {
		return
	}
	
	FL_SEARCH_OK := false	
	
	switch {
	
		case p.FL_SHARED == "Y" && (p.DOC_STAT == "Premium" || p.DOC_STAT == "Worldwide" || p.DOC_STAT == "ULAPPH Only" || (p.DOC_STAT == "Personal" && p.DOC_AUTHOR == uid)):
			FL_SEARCH_OK = true
 
		case p.FL_SHARED == "Y" && (p.DOC_STAT == "Premium" || p.DOC_STAT == "Worldwide" || p.DOC_STAT == "ULAPPH Only"):
			FL_SEARCH_OK = true
			
		case p.DOC_AUTHOR == uid:
			FL_SEARCH_OK = true
			
		case p.FL_SHARED == "Y" && (p.DOC_STAT == "Premium" || p.DOC_STAT == "Worldwide" || p.DOC_STAT == "ULAPPH Only" || p.DOC_AUTHOR == uid):
			FL_SEARCH_OK = true
			
		case p.DOC_AUTHOR == uid:
			FL_SEARCH_OK = true
			
		case p.FL_SHARED == "Y" && (p.DOC_STAT == "Premium" || p.DOC_STAT == "Worldwide"):
			FL_SEARCH_OK = true	
	}
	
	if FL_SEARCH_OK == true {
		
		switch {
			
			case IDX_TARGET == "IDX_TDSSLIDE" || IDX_TARGET == "IDX_TDSARTL":
				SPL := strings.Split(SID,"-")
				DOC_ID := "0"
				if len(SPL) > 1 {
					DOC_ID = SPL[1]
				}
				docID := str2int(DOC_ID)
				
				blobkey := ""
				if IDX_TARGET == "IDX_TDSSLIDE" {
					blobkey, _, _, _, _, _, _, _, _, _  = getTDSSLIDEBlobKey(w, r, docID)
				} else {
					blobkey, _, _, _, _, _, _, _, _ = getTDSARTLBlobKey(w, r, docID)
				}
				blobByte := getBlobByte(w, r, blobkey)
 
				//for every section, add to index
				scanner := bufio.NewScanner(bytes.NewReader(blobByte))
				thisText := ""
				i := 0
				l := 0
				for scanner.Scan() {
					l++
					if scanner.Text() != "" {
						thisStr := fmt.Sprintf("%v", scanner.Text())
						if string(thisStr[0]) == "*" {
							if thisText != "" {
								d := Bleve {
									Name: scanner.Text(),
									Text: thisText,
								}
								// index some data
								inBlev.Index(fmt.Sprintf("id%v:line%v",i,l), d)								
							}
							thisText = ""
							i++
						} else {
							thisText = fmt.Sprintf("%v\n%v", thisText, scanner.Text())
						}
					}
				}			
		
			default:
			
				d := Bleve {
					Name: "TDSMEDIA",
					Text: fmt.Sprintf("%v - %v", p.DOC_TITLE, p.DOC_DESC),
				}
								
				// index some data
				inBlev.Index("id", d)
				
				d = Bleve {
					Name: "TDSMEDIA",
					Text: p.DOC_CONTENT_TEXT,
				}
				
				// index some data
				inBlev.Index("id2", d)
		}
 
		// search for some text
		query := bleve.NewMatchQuery(SEARCH_KEY)
		search := bleve.NewSearchRequest(query)
		search.Highlight = bleve.NewHighlight()
		searchResults, err := inBlev.Search(search)
		if err != nil {
			//fmt.Println(err)
			return
		}
		bleveSearchOut(w,r,uid,IDX_TARGET,SID,SEARCH_KEY,p.DOC_TITLE, p.DOC_DESC,p.CONTENT_URL,searchResults)
		
	} else {
		fmt.Fprintf(w, "Insufficient privilege to search this document!")
	}
	
}

