//Display humanized form for the timestamp like 2006-01-02 15:04:05.999999999 -0700 MST
func nowHumanize(date string) (fTime string) {
	if date != "" {
		t2, err := time.Parse("2006-01-02 15:04:05.999999999 -0700 MST", date)
			if err != nil {
					fmt.Println("parse error", err.Error())
			}
		fTime = fmt.Sprintf("%s", humanize.Time(t2))
	} else {
		fTime = "unknown"
	}
	return fTime
}

//Display humanized form of timestamp like 20150316022627 
func stmpHumanize(tStr string) (fTime string) {
	if tStr != "" {
		//tStr := "20150316022627"
		t := strings.Split(tStr,"")
		if len(t) != 14 {
			return tStr 
		}
		date := fmt.Sprintf("%s%s%s%s-%s%s-%s%s %s%s:%s%s:%s%s +0000 UTC", t[0], t[1], t[2], t[3], t[4], t[5], t[6], t[7], t[8], t[9], t[10], t[11], t[12], t[13])
		//fmt.Printf("%v\n", date)
		t2, err := time.Parse("2006-01-02 15:04:05 +0000 UTC", date)
		if err != nil {
				fmt.Println("parse error", err.Error())
		}
		fTime = fmt.Sprintf("%s", humanize.Time(t2))
	} else {
		fTime = "unknown"
	}
	return fTime
}
		
//validations for access
//it determines if the current user is a valid or registered user
func validateAccess(w http.ResponseWriter, r *http.Request, FUNC_CODE, lref string) (FL_PROCEED_OK bool) {
	c := appengine.NewContext(r)
	u := user.Current(c)
	uid := ""
	if u == nil {
		if lref == "" {
			promptLogin(w,r,"ULAPPH")
		} else {
			redURL := fmt.Sprintf("/login?continue=%v", url.QueryEscape(lref))
			http.Redirect(w, r, redURL, http.StatusFound)
		}
		//return
	} else {
		uid = u.Email
	}
	if uid == "" {
		if u != nil {
			uid = u.Email
		}
	}
	FL_PROCEED_OK = false
	switch FUNC_CODE {
		case "IS_VALID_USER":
			FUNC_CODE := "GET_GRP_ID"
			FL_VALID_USER, _, _  , _ := usersProcessor(w, r, "au", uid, FUNC_CODE)
			if FL_VALID_USER == true {
				FL_PROCEED_OK = true
			}else {
				msgDtl := fmt.Sprintf("[U00167b] ERROR: Access is not allowed for this session!")
				msgTyp := "error"
				msgURL := fmt.Sprintf("https://ulapph-public-1.appspot.com")
				action := fmt.Sprintf("Apologies for this issue.")
				sysReq := fmt.Sprintf("/sysmsg?msgTyp=%v&message=%v&msgURL=%v&action=%v", msgTyp, msgDtl, msgURL, action)
				http.Redirect(w, r, sysReq, http.StatusFound)
				return
			}
	}
	//prevents bots
	FL_IS_BOT := isBot(w,r)
	if FL_IS_BOT == true && FL_PROCEED_OK != true {
		//if this is an api call, allow
		if isValidApiCall(w,r) == true {
			FL_PROCEED_OK = true
		} else {
			msgDtl := fmt.Sprintf("[U00167] ERROR: Hi!, You appear to us as a bot! We currently disallow robots from accessing some parts of the website. Thanks!")
			msgTyp := "error"
			msgURL := fmt.Sprintf("https://ulapph-public-1.appspot.com")
			action := fmt.Sprintf("Apologies for this issue.")
			sysReq := fmt.Sprintf("/sysmsg?msgTyp=%v&message=%v&msgURL=%v&action=%v", msgTyp, msgDtl, msgURL, action)
			http.Redirect(w, r, sysReq, http.StatusFound)
			return
		}
	}
	return FL_PROCEED_OK
}

//checks if it is a valid API call 
func isValidApiCall(w http.ResponseWriter, r *http.Request) (FL_PROCEED_OK bool) {
	API_KEY_R := r.Header["Authorization"]
	API_KEY := ""
	FL_PROCEED_OK = false
	if len(API_KEY_R) > 0 {
		if API_KEY_R[0] == "" {
			API_KEY = r.FormValue("API_KEY")
		} else {
			API_KEY = API_KEY_R[0]
		}
	} else {
		API_KEY = r.FormValue("API_KEY")
	}
	if THINGS_API_KEY == API_KEY {
		FL_PROCEED_OK = true
	}
	return FL_PROCEED_OK
}
 
