	usersOnline_CACHE_KEY := fmt.Sprintf("USERS_ONLINE_LIST")
	usersOnline_CACHE := getStrMemcacheValueByKey(w,r,usersOnline_CACHE_KEY)
	fuCtr := 0
	if usersOnline_CACHE != "" {
		SPL := strings.Split(usersOnline_CACHE,",")
		for i := 0; i < len(SPL); i++ {
			if SPL[i] != "" {
				fuCtr++
			}
		}
	}
	if fuCtr > 0 {
		return true
	}
	return false
