			u, err := blobstore.UploadURL(c, csn2, nil)
			if err != nil {
				return
			}
 
			var m bytes.Buffer
			fw := multipart.NewWriter(&m)
			file, err := fw.CreateFormFile("file", "WGET-URL")
			if err != nil {
				return
			}
		
			if _, err = file.Write(urlRespBytes); err != nil {
				return
			}
			contentType := http.DetectContentType(urlRespBytes)
			
 
			
			_ = fw.WriteField("UID", UID)
