//check if user is online; otherwise stop running some crons
func sysChecker(w http.ResponseWriter, c appengine.Context, FUNC_CODE string) (FL_RET_STAT bool, SYS_VAL_NUM int, SYS_VAL_TXT string) {
	mkey := "TOT_ONLINE"
	//TOT_ONLINE := 0
	FL_RET_STAT = false
	key := datastore.NewKey(c, "TDSSTATS", mkey, 0, nil)
	q := datastore.NewQuery("TDSSTATS").Filter("__key__ =", key)
	//c.Errorf("[S0534]")
	stats := make([]TDSSTATS, 0, 1)
	if _, err := q.GetAll(c, &stats); err != nil {
		panic(err)
	}
	//OnlineUsers := 0
	for _, p := range stats {
		if p.STAT_NAME == mkey {
			SYS_VAL_NUM = p.VAL_NUM
			FL_RET_STAT = true
		}		
	}
		
	return FL_RET_STAT, SYS_VAL_NUM, SYS_VAL_TXT
}
 
