//Url Shortener
//shortens a URL based on Google url shortener service
//an integration with the Google Shortner API
func ShortenUrl(w http.ResponseWriter, r *http.Request, longUrl string) (shortenUrl string) {
	c := appengine.NewContext(r)

	if SYS_ENABLE_URL_SHORTENER  == false {
		return longUrl
	}
	
	var encbuf bytes.Buffer
	enc := json.NewEncoder(&encbuf)
	err := enc.Encode(map[string]string{"longUrl": longUrl})
	if err != nil {
		//return
		shortenUrl = longUrl
 
	}
	
	transport := &transport.APIKey{
			Key:       apiKeyUs,
			Transport: &urlfetch.Transport{Context: c}}
	client := &http.Client{Transport: transport}
	res, err := client.Post("https://www.googleapis.com/urlshortener/v1/url", "application/json", strings.NewReader(encbuf.String()))
	if err != nil {
		//return
		shortenUrl = longUrl
	}
	if res.StatusCode != 200 {
		//err = os.NewError("failed to post")
		//return
		shortenUrl = longUrl
 
	}
	b, err := ioutil.ReadAll(res.Body)
	if err != nil {
		//return
		shortenUrl = longUrl
	}
	var decbuf bytes.Buffer
	decbuf.Write(b)
	dec := json.NewDecoder(&decbuf)
	var out map[string]interface{}
	err = dec.Decode(&out)
	if err != nil {
		//return
		shortenUrl = longUrl
 
	}
	shortenUrl = out["id"].(string)
 
	return
}
 
