func (d datapoint) MarshalJSON() ([]byte, error) {
	lat := fmt.Sprintf("%f", d.Lat)
	lon := fmt.Sprintf("%f", d.Lon)
	jsonResult :=  `{"latitude":"` + lat + `", "longitude":"` + lon + `", "time":"` + d.Time + `"}`
	return []byte(jsonResult), nil
}
 
