//handles TODOs processing
func handleTodos(c appengine.Context, r *http.Request, uid string) (interface{}, error) {
	switch r.Method {
	case "POST":
		todo, err := decodeTodo(r.Body)
		if err != nil {
			return nil, err
		}
		return todo.save(c,uid)
	case "GET":
		return getAllTodos(c,uid)
	case "DELETE":
		return nil, deleteDoneTodos(c,uid)
	}
	return nil, fmt.Errorf("method not implemented")
}
 
//retrieves default todo list
func defaultTodoList(c appengine.Context, uid string) *datastore.Key {
	//return datastore.NewKey(c, "TodoList", "default", 0, nil)
	return datastore.NewKey(c, "TodoList", uid, 0, nil)
}
 
//gets the key for a todo item
func (t *Todo) key(c appengine.Context, uid string) *datastore.Key {
	if t.Id == 0 {
		t.Created = time.Now()
		return datastore.NewIncompleteKey(c, "Todo", defaultTodoList(c,uid))
	}
	return datastore.NewKey(c, "Todo", "", t.Id, defaultTodoList(c,uid))
}
 
//saves the details of a todo item
func (t *Todo) save(c appengine.Context,uid string) (*Todo, error) {
	k, err := datastore.Put(c, t.key(c,uid), t)
	//c.Errorf("[S0640]")
	if err != nil {
		return nil, err
	}
	t.Id = k.IntID()
	return t, nil
}
 
//decode a todo data
func decodeTodo(r io.ReadCloser) (*Todo, error) {
	defer r.Close()
	var todo Todo
	err := json.NewDecoder(r).Decode(&todo)
	return &todo, err
}
 
//gets all todos
func getAllTodos(c appengine.Context, uid string) ([]Todo, error) {
	todos := []Todo{}
	ks, err := datastore.NewQuery("Todo").Ancestor(defaultTodoList(c, uid)).Order("Created").GetAll(c, &todos)
	//c.Errorf("[S0641]")
	if err != nil {
		return nil, err
	}
	for i := 0; i < len(todos); i++ {
		todos[i].Id = ks[i].IntID()
	}
	return todos, nil
}
 
//deletes done todos
func deleteDoneTodos(c appengine.Context, uid string) error {
	return datastore.RunInTransaction(c, func(c appengine.Context) error {
		ks, err := datastore.NewQuery("Todo").KeysOnly().Ancestor(defaultTodoList(c,uid)).Filter("Done=", true).GetAll(c, nil)
		//c.Errorf("[S0642]")
		if err != nil {
			return err
		}
		return datastore.DeleteMulti(c, ks)
	}, nil)
}
 
