			msgDtl := fmt.Sprintf("New comment posted on: %v at %v by %v. LINK: %v", SID, SYS_SERVER_NAME, uid, ShortenUrl(w,r,fmt.Sprintf("https://ulapph-public-1.appspot.com/captcha?CC_FUNC=DISP&R=COMMENT&SID=%v&TITLE=", SID, "")))
