//D0061
//Extract comments 
func extractComments(w http.ResponseWriter, r *http.Request, SID, TITLE string) (err error) {
	c := appengine.NewContext(r)

	////c.Infof("SID: %v",SID)
	var file *xlsx.File
	var sheet *xlsx.Sheet
	var row *xlsx.Row
	var cell *xlsx.Cell

	file = xlsx.NewFile()
	sheet, err = file.AddSheet(SID)
	if err != nil {
		panic(err)
	}

	//Add row header
	row = sheet.AddRow()
	cell = row.AddCell()
	cell.Value = "NAME"
	cell = row.AddCell()
	cell.Value = "VALUE"
	cell = row.AddCell()
	cell.Value = "SID"
	cell = row.AddCell()
	cell.Value = "UID"
	cell = row.AddCell()
	cell.Value = "COMMENT"
	cell = row.AddCell()
	cell.Value = "TIMESTAMP"
	cell = row.AddCell()
	cell.Value = "CID"
	cell = row.AddCell()
	cell.Value = "DEPTH"
	cell = row.AddCell()
	cell.Value = "LATLON"
	cell = row.AddCell()
	cell.Value = "PARENT"
	cell = row.AddCell()
	cell.Value = "URL"

	q := datastore.NewQuery("TDSCOMMENT").Filter("SID =", SID)
	recCount,_ := q.Count(c)
	cmts := make([]TDSCOMMENT, 0, recCount)
	if _, err := q.GetAll(c, &cmts); err != nil {
		 panic(err)
	}
	if recCount > 0 {
		for _, p := range cmts{
			if p.DEPTH != 1 {
				continue
			}
			sValue := float64(0)
			thisName := p.NAME
			SPL := strings.Split(p.COMMENT, " got a score of ")
			if p.SVAL != "" {
				sValue, _ = strconv.ParseFloat(p.SVAL, 64)
				if len(SPL) > 0 {
					thisName = SPL[0]
				}
			} else {
				//old enteries parsing
				if len(SPL) > 0 {
					sValue, _ = strconv.ParseFloat(SPL[1], 64)
					thisName = SPL[0]
				}
			}
			row = sheet.AddRow()
			cell = row.AddCell()
			cell.Value = thisName
			cell = row.AddCell()
			cell.SetFloat(sValue)
			cell = row.AddCell()
			cell.Value = p.SID
			cell = row.AddCell()
			cell.Value = p.UID
			cell = row.AddCell()
			cell.Value = p.COMMENT
			cell = row.AddCell()
			cell.Value = fmt.Sprintf("%v", p.TIMESTAMP)
			cell = row.AddCell()
			//cell.Value = fmt.Sprintf("%v", p.CID)
			cell.SetInt(p.CID)
			cell = row.AddCell()
			cell.SetInt(p.DEPTH)
			cell = row.AddCell()
			cell.Value = p.LATLON
			cell = row.AddCell()
			cell.SetInt(p.PARENT)
			cell = row.AddCell()
			cell.Value = p.URL
		}
	}
	expName := fmt.Sprintf("%v %v.xlsx", SID, TITLE)
	w.Header().Set("Content-Disposition", fmt.Sprintf("attachment; filename=%v", expName))
	w.Header().Set("Content-Type", "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet")
	w.WriteHeader(200)
	file.Write(w)
	return err
}

func Shuffle(slice interface{}) {
    rv := reflect.ValueOf(slice)
    swap := reflect.Swapper(slice)
    length := rv.Len()
    for i := length - 1; i > 0; i-- {
            j := rand.Intn(i + 1)
            swap(i, j)
    }
}

